import {Component, ViewChild} from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatSidenav } from '@angular/material/sidenav';
import { InvoiceTypeComponent } from './invoice-type/invoice-type.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'ProyectTwo';
  tituloAEnviar = 'Titulo enviado desde el padre al hijo';

  @ViewChild('sidenav') sidenav: MatSidenav | undefined;

  constructor(
    private dialog: MatDialog,  //Etiqueta para abrir un dialogo
  ) { }

  openDialog(){   //para NUEVO Y PARA EDITAR es opcionacil ? no estoy oglbiado
    this.dialog.open(InvoiceTypeComponent, {
      width: '450px',
      height: '450px'
    });
  }

  handleNewInvoiceClick() {
    if (this.sidenav) {
      this.sidenav.toggle(); // Cierra o abre el sidenav
    }
    this.openDialog();
  }

}

<app-calculadora [tituloARecibir]="tituloAEnviar"></app-calculadora>
